mapper.select:
SELECT * FROM ( SELECT A.ADBOARDNUM ADBOARDNUM ,A.MEMNUM MEMNUM ,A.ADBOARDTITLE ADBOARDTITLE ,A.DELETEYN DELETEYN ,TO_CHAR(A.INSERTDATE, 'YYYY-MM-DD') INSERTDATE ,A.ADBOARDHITS ADBOARDHITS
,CEIL(ROW_NUMBER() OVER(ORDER BY A.ADBOARDNUM DESC)/ #{pageSize} ) pageSize ,COUNT(A.ADBOARDNUM) OVER() AS totalCount FROM ADBOARD A WHERE A.DELETEYN = 'Y' ORDER BY TO_NUMBER(ADBOARDNUM) DESC
) WHERE pageSize = #{curPage}

mapper.update:

	UPDATE  ADBOARD
   	   SET	ADBOARDCOMENT    = #{adboardcoment, jdbcType=VARCHAR}
   	   	   ,ADBOARDTITLE	 = #{adboardtitle, jdbcType=VARCHAR}
	  	   ,UPDATEDATE 	= SYSDATE
	WHERE   DELETEYN = 'Y'
	AND     ADBOARDNUM = #{adboardnum, jdbcType=VARCHAR}
