mapper.select:
SELECT * FROM ( SELECT H.HOTELNUM HOTELNUM, H.REGIONID REGIONID, H.HOTELNAME HOTELNAME, H.HOTELTEL HOTELTEL, H.HOTELIMAGE HOTELIMAGE, H.HOTELPRICE HOTELPRICE, H.HOTELADRESS HOTELADRESS, H.HOTELCOMENT HOTELCOMENT, H.HOTELMAPX HOTELMAPX, H.HOTELMAPY HOTELMAPY, H.HOTELCHECKIN HOTELCHECKIN, H.HOTELCHECKOUT HOTELCHECKOUT, H.INSERTDATE INSERTDATE, H.UPDATEDATE UPDATEDATE, H.DELETEYN DELETEYN,
CEIL(ROW_NUMBER() OVER(ORDER BY H.HOTELNAME) / #{pageSize} ) pageSize, COUNT(H.HOTELNAME) OVER() AS totalCount FROM HOTEL H WHERE H.DELETEYN='Y' ORDER BY 1 DESC
) WHERE pageSize = #{curPage}
