mapper.select:
SELECT * FROM( SELECT A.TRIPNUM TRIPNUM, A.TRIPNAME TRIPNAME, A.TRIPCATALOGUE TRIPCATALOGUE, A.TRIPCOMENT TRIPCOMENT, A.TRIPREGION TRIPREGION, A.TRIPIMAGE TRIPIMAGE, TO_CHAR(A.INSERTDATE, 'YYYY-MM-DD') INSERTDATE, TO_CHAR(A.UPDATEDATE, 'YYYY-MM-DD') UPDATEDATE, A.DELETEYN DELETEYN, A.SPOTHIT SPOTHIT, CEIL(ROW_NUMBER() OVER(ORDER BY A.TRIPNUM DESC) / #{pageSize} ) pageSize, COUNT(A.TRIPNUM) OVER() AS totalCount FROM TRIP A WHERE A.DELETEYN = 'Y'
AND A.TRIPREGION LIKE '%' || #{keyword, jdbcType=VARCHAR} || '%'
AND A.TRIPCATALOGUE LIKE '%' || #{keyword, jdbcType=VARCHAR} || '%'
ORDER BY SPOTHIT DESC, TRIPNUM ASC
) WHERE pageSize = #{curPage}
